import randomdef manualMove(pcolors,currentPlayer,board):    row=7    col=7    fourInRow=False    #check if I have four in a row    for x in range(15):        for y in range(15):            if board[x][y]==currentPlayer:                win=[0,0]                #row                if x<11:                    count=1                    for offset in range(1,5,1):                        if board[x][y]==board[x+offset][y]:                            count+=1                        if board[x+offset][y]==0:                            win=[x+offset,y]                        elif board[x+offset][y]!=currentPlayer:                            count=1                    if count==4:                        row=win[0]                        col=win[1]                        fourInRow=True                                        #col                if y<11:                    count=1                    for offset in range(1,5,1):                        if board[x][y]==board[x][y+offset]:                            count+=1                        if  board[x][y+offset]==0:                            win=[x,y+offset]                        elif board[x][y+offset]!=currentPlayer:                            count=1                    if count==4:                        row=win[0]                        col=win[1]                        fourInRow=True                                            #left to right diag                if x<11 and y<11:                    count=1                    for offset in range(1,5,1):                        if board[x][y]==board[x+offset][y+offset]:                            count+=1                        if  board[x+offset][y+offset]==0:                            win=[x+offset,y+offset]                        elif board[x+offset][y+offset]!=currentPlayer:                            count=1                    if count==4:                        row=win[0]                        col=win[1]                        fourInRow=True                                #right to left diag                if x<11 and y>3:                    count=1                    for offset in range(1,5,1):                        if board[x][y]==board[x+offset][y-offset]:                            count+=1                        if  board[x+offset][y-offset]==0:                            win=[x+offset,y-offset]                        elif board[x+offset][y-offset]!=currentPlayer:                            count=1                    if count==4:                        row=win[0]                        col=win[1]                        fourInRow=True                            #random place near opponents peg    empty=False    if fourInRow==False:        emptyCellsList=[]        for x in range (1,14):            for y in range(1,14):                if board[x][y]!=0 and board[x][y]!=currentPlayer:                    for a in range(-1,2,1):                        for b in range(-1,2,1):                            row=x+a                            col=y+b                            if board[row][col]==0:                                emptyCellsList+=[row,col]        if len(emptyCellsList)!=0:            a=random.randrange((len(emptyCellsList)//2))            row=emptyCellsList[2*a]            col=emptyCellsList[(2*a)+1]        else:            row=7            col=7    return row,col##           emptyCellsList.append(chr(65+row)+str(col))##            if emptyCellsList==[]:##                row=7##                col=7##                return row, ##            move=emptyCellsList[random.randrange(0,len(emptyCellsList))]##            row=ord(move[0])-65##            col=int(move[1:])##            if board[row][col]==0:##                return row,col